import React from 'react';
import TaskListRow from './TaskListRow';
import TaskListNewRow from './TaskListNewRow';

export default class TaskList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            editTask: false,
        }
    }

    handlerSubmitEditClick = (i, text) => {
        const taskListEdited = [...this.state.taskList];
        taskListEdited[i].desc = text;
        this.setState({ taskList: taskListEdited, showNewRow: false })
    }

    handlerCancelEditClick = () => {

    }

    render() {
        return (
            <div className={'Task-list'}>
                {this.props.showNewRow &&
                    <TaskListNewRow
                        handlerSubmitCreationClick={this.props.handlerSubmitCreationClick}
                        handlerCancelCreationClick={this.props.handlerCancelCreationClick}
                    />
                }
                <span className={'Task-list list element'}>
                    {
                        this.props.taskList.map(e => {
                            return (
                                <TaskListRow
                                    editTask={this.state.editTask}
                                    desc={e.desc}
                                    handlerSubmitEditClick={this.handlerSubmitEditClick}
                                    handlerCancelEditClick={this.handlerCancelEditClick}
                                />
                            );
                        })
                    }
                </span>
            </div>
        );
    }
}
