import React from 'react';
//import TaskListRow from './TaskListRow';
import TaskListNewRow from './TaskListNewRow';

export default class TaskList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            editTask: false,
        }
    }

    render() {
        return (
            <div className={'Task-list'}>
                {this.props.showNewRow &&
                    <TaskListNewRow
                        handlerSubmitCreationClick={this.props.handlerSubmitCreationClick}
                        handlerCancelCreationClick={this.props.handlerCancelCreationClick}
                    />
                }
                <span className={'Task-list list element'}>
                    <TaskListRow
                        editTask={this.state.editTask}
                    />
                </span>
            </div>
        );
    }
}
