import React from 'react'

function TaskListRow(props) {
    return (
        <div className={'row'}>
            {props.editTask
                ? <input
                    value={props.desc}
                    onChange={props.handlerDescriptionChange}
                />
                : <span>{props.taskList.desc}</span>}
            <button
                onClick={props.handlerSubmitEditingClick}
            >
                Submit
            </button>
            <button
                onClick={props.handlerCancelEditClick}
            >
                Cancel
            </button>
        </div>
    );
}

export default TaskListRow;
