import React from 'react'

export class TaskListNewRow extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            desc: ''
        }
    }

    handlerDescChange = (e) => {
        this.setState({ desc: e })
    }

    render() {
        return (
            <div className={'edit-row'}>
                <input
                    value={this.state.desc}
                    type={'text'}
                    onChange={(e) => this.handlerDescChange(e.target.value)}
                />
                <button
                    onClick={this.props.handlerSubmitCreationClick}
                >
                    Create
            </button>
                <button
                    onClick={this.props.handlerCancelCreationClick}
                >
                    Cancel
            </button>
            </div>
        );
    }
}
