import React from 'react';
import TaskListComponent from './TaskListComponent'

export default class TaskListContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            taskList: [{ desc: String, done: Boolean }],
            showNewRow: false
        }
    }

    handlerAddNewClick = () => {
        this.setState({ showNewRow: true });
    }

    handlerSubmitCreationClick = () => {

    }

    handlerCancelCreationClick = () => {
        this.setState({ showNewRow: false });
    }

    handlerDescChange = (text) => {

    }

    handlerSubmitEditClick = (i, text) => {
        const taskListEdited = [...this.state.taskList];
        taskList[i].desc = text;
        this.setState({ taskList: taskListEdited, showNewRow: false })
    }

    handlerCancelEditClick = () => {

    }

    render() {
        return (
            <div className={'task-list-container'}>
                <button
                    className={'btn add-new-task'}
                    onClick={this.handlerAddNewClick}
                >
                    <span className={'glyphicon glyphicon-plus'}></span>
                    Add new task
                </button>
                <div className={'task-list-component'}>
                    <TaskListComponent
                        handlerDescChange={this.handlerDescChange}
                        handlerSubmitCreationClick={this.handlerSubmitCreationClick}
                        handlerCancelCreationClick={this.handlerCancelCreationClick}
                        showNewRow={this.state.showNewRow}
                    />
                </div>
            </div >
        );
    }
}
